mapper.select:
SELECT ct.tool_id AS toolId, ct.user_id AS userId, ct.tool_name AS toolName, ct.tool_explain AS toolExplain, ct.tool_url AS toolUrl, ct.category_type AS categoryType, um.nickname AS userNickname, cc.category_name AS categoryTypeName from common_tool ct left join user_msg um on ct.user_id = um.user_id left join common_category cc on ct.category_type = cc.category_type where
and ct.create_time >= #{dto.fromTime}
and ct.create_time <= #{dto.toTime}
ct.tool_id > #{dto.start} * #{dto.count} order by ct.tool_id limit #{dto.count}

SELECT count(*) from common_tool


        and create_time >= #{dto.fromTime}


        and create_time <= #{dto.toTime}
